ON["books"])){			if(sizeof($_SESSION["books"]) > 0){				return 1;			}else{				return 0;			}		}else{			return 0;		}		return 0;	} ?>  <!DOCTYPE html PUBLIC "-//W3C//DTD HTML 4.01//EN" "http://www.w3.org/TR/html4/strict.dtd"><html> <script type="text/javascript">function openWyslijConfirmation(){	window.open("wyslij_koszyk.php","potwierdzenie","menubar=0,resizable=0,width=650,height=650");}</script><head><meta content="text/html; charset=UTF-8" http-equiv="content-type"><title>Aksim.pl - koszyk</title><link href="style.css" rel="stylesheet" type="text/css"></head><body>	<div class="basketInfoDiv">	<?php		$fd = fopen("header.html", "r");		$footer = fread($fd, filesize("header.html"));		fclose($fd);		echo $footer;	?>		<div class="emptyDiv2"></div>	<div  class="basketTableMainHeader"> <?php if(isBasketNotEmpty()){echo("Tw&#243;j koszyk:");}else{echo("Tw&#243;j koszyk jest pusty");} ?></div>	<div class="emptyDiv2"></div>		<div class="basketDivConf">Promocja. Wysy&#322;ka gratis</div>	<div class="emptyDiv1"></div>			<?php			$books_array = $_SESSION["books_array"];			if(isBasketNotEmpty()){							while (list($book_id, $book_amount) = each($_SESSION["books"])) {					//print_r($book);					//print_r($book_id);					echo("<table class='basketTable'><tbody>");					echo("<tr><td width='89' align='left' valign='top'>");					echo("<img class='imgClass' src='".$books_array["book_".$book_id."_image"]."' width='150' height='214'><br>");					echo("<span class='BASKET'>Cena: ".$books_array["book_".$book_id."_price"]." z&#322;</span></td>");					echo("<td width='687' align='left' valign='top' >");					echo("<span class='TXT2'>".$books_array["book_".$book_id."_title"]."</span><br>");					echo("<span class='TXT3'>Ilo&#347;&#263;: ".$book_amount."</span><br>");					echo("<span class='TXT3'>&#321;&#261;czna cena: ".($book_amount * $books_array["book_".$book_id."_price"])." z&#322;</span><br>");					echo("<form action='koszyk.php' meth